import React, { useState, useEffect } from 'react';
import './App.css';

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Get user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (err) => {
          setError('Unable to retrieve your location. Please enable location services.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h2>Nearby Dermatologists</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {location ? (
          <div style={{ display: 'flex', flexDirection: 'row', gap: '20px' }}>
            {/* Map Frame */}
            <iframe
              title="Nearby Dermatologists"
              src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
              width="60%"
              height="500"
              style={{ border: 0 }}
              allowFullScreen=""
              loading="lazy"
            ></iframe>

            {/* Dermatologist Details */}
            <div style={{ width: '40%', textAlign: 'left' }}>
              <h3>Dermatologist Details</h3>
              <p>
                Use the map to find dermatologists near your location. Click on the markers to view details and contact information.
              </p>
              <p>
                For detailed information, interact with the map to explore nearby dermatologists and their contact details.
              </p>
            </div>
          </div>
        ) : (
          !error && <p>Loading your location...</p>
        )}
      </header>
    </div>
  );
}

export default NearbyDermatologists;
