import React, { useState, useEffect } from 'react';

const styles = {
  container: {
    display: 'flex',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#000000', // Black background
  },
  leftPanel: {
    flex: 2,
    color: 'white',
    padding: '20px',
    textAlign: 'center',
  },
  rightPanel: {
    flex: 1,
    backgroundImage: 'url("/1.jpg")', // Use the image as background
    backgroundSize: 'auto', // Preserve original size
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'calc(100% - 50px) center', // Slightly center from the right edge
  },
  iframe: {
    width: '100%',
    height: '500px',
    border: 'none',
    marginTop: '20px',
  },
  error: {
    color: 'red',
  },
};

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        () => {
          setError('Unable to retrieve your location. Please enable location services.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.leftPanel}>
        <header>
          <h2>Nearby Dermatologists</h2>
          {error && <p style={styles.error}>{error}</p>}
          {location ? (
            <>
              <p>Find dermatologists near your location using the map below:</p>
              <iframe
                title="Nearby Dermatologists"
                src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
                style={styles.iframe}
                allowFullScreen
                loading="lazy"
              ></iframe>
            </>
          ) : (
            !error && <p>Loading your location...</p>
          )}
        </header>
      </div>
      <div style={styles.rightPanel}></div>
    </div>
  );
}

export default NearbyDermatologists;
