import React from 'react';

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    color: 'white',
    backgroundImage: 'url("/1.jpg")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
    paddingTop: '80px', // Add padding to account for the fixed header
  },
  header: {
    position: 'fixed', // Make the header fixed
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000, // Ensure it stays above other content
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#61dafb',
    background: 'rgba(0, 0, 0, 0.8)', // Semi-transparent background
    padding: '15px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  list: {
    textAlign: 'left',
    margin: '20px auto',
    maxWidth: '600px',
    color: '#ddd',
  },
};

const diseaseFoodRecommendations = {
  "Tinea corporis (Ringworm)": [
    "Include foods rich in vitamins A, C, and E to boost the immune system.",
    "Consume probiotics to support gut health and immunity.",
    "Avoid sugary foods that can promote fungal growth.",
    "Incorporate garlic and turmeric for their antifungal properties.",
  ],
  "Eczema": [
    "Include anti-inflammatory foods like fatty fish, flaxseeds, and walnuts.",
    "Consume foods high in probiotics, such as yogurt and kefir.",
    "Avoid common allergens like dairy, gluten, and nuts.",
    "Stay hydrated to maintain skin moisture.",
  ],
  "Psoriasis": [
    "Include omega-3 fatty acids from fish oil or flaxseeds.",
    "Consume plenty of fruits and vegetables for their antioxidant properties.",
    "Avoid processed foods, red meat, and dairy products.",
    "Incorporate turmeric and ginger for their anti-inflammatory effects.",
  ],
  "Acne": [
    "Include foods with a low glycemic index to regulate blood sugar levels.",
    "Consume foods rich in antioxidants, such as berries and leafy greens.",
    "Avoid dairy products and sugary foods.",
    "Incorporate zinc-rich foods like nuts and seeds.",
  ],
  // Add more disease-specific recommendations here
};

function FoodRecommendation({ diseaseName }) {
  const recommendations = diseaseName ? diseaseFoodRecommendations[diseaseName] || ["No specific recommendations found for this condition."] : ["No disease specified."];

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2>Food Recommendations</h2>
      </header>
      <p>Based on your skin condition, here are some dietary recommendations:</p>
      <ul style={styles.list}>
        {recommendations.map((recommendation, index) => (
          <li key={index}>{recommendation}</li>
        ))}
      </ul>
    </div>
  );
}

export default FoodRecommendation;
