import React, { useState, useEffect } from 'react';
import './App.css';

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);
  const [dermatologists, setDermatologists] = useState([]);

  useEffect(() => {
    // Get user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const userLocation = {
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          };
          setLocation(userLocation);

          // Fetch dermatologist details using Google Places API
          try {
            const response = await fetch(
              `https://maps.googleapis.com/maps/api/place/nearbysearch/json?location=${userLocation.latitude},${userLocation.longitude}&radius=5000&type=doctor&keyword=dermatologist&key=YOUR_API_KEY`
            );
            const data = await response.json();
            if (data.results) {
              const dermatologistDetails = data.results.map((place) => ({
                id: place.place_id,
                name: place.name,
                address: place.vicinity,
                rating: place.rating || 'N/A',
                contact: place.formatted_phone_number || 'N/A',
              }));
              setDermatologists(dermatologistDetails);
            } else {
              setError('No dermatologists found nearby.');
            }
          } catch (err) {
            setError('Failed to fetch dermatologist details.');
          }
        },
        (err) => {
          setError('Unable to retrieve your location. Please enable location services.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, []);

  const handleAppointment = (dermatologist) => {
    alert(`Appointment booked with ${dermatologist.name} at ${dermatologist.address}.`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Nearby Dermatologists</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {location ? (
          <>
            <p>Find dermatologists near your location using the map below:</p>
            <iframe
              title="Nearby Dermatologists"
              src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
              width="100%"
              height="500"
              style={{ border: 0 }}
              allowFullScreen=""
              loading="lazy"
            ></iframe>
            <h3>Dermatologist Details</h3>
            <ul>
              {dermatologists.length > 0 ? (
                dermatologists.map((dermatologist) => (
                  <li key={dermatologist.id}>
                    <p><strong>{dermatologist.name}</strong></p>
                    <p>Address: {dermatologist.address}</p>
                    <p>Rating: {dermatologist.rating}</p>
                    <button onClick={() => handleAppointment(dermatologist)}>Book Appointment</button>
                  </li>
                ))
              ) : (
                <p>Loading dermatologist details...</p>
              )}
            </ul>
          </>
        ) : (
          !error && <p>Loading your location...</p>
        )}
      </header>
    </div>
  );
}

export default NearbyDermatologists;
