import React, { useState } from 'react';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({ apiKey: process.env.REACT_APP_GOOGLE_GENAI_API_KEY });

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    color: 'white',
    backgroundImage: 'url("/1.jpg")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    minHeight: '100vh',
    paddingTop: '80px', // Add padding to account for the fixed header
  },
  header: {
    position: 'fixed', // Make the header fixed
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000, // Ensure it stays above other content
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#61dafb',
    background: 'rgba(0, 0, 0, 0.8)', // Semi-transparent background
    padding: '15px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
};

function CausePrediction({ diseaseName }) {
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResponse(null);

    const question1 = e.target.elements.question1.value;
    const question2 = e.target.elements.question2.value;

    const prompt = `
      Disease: ${diseaseName}
      Question 1: ${question1}
      Question 2: ${question2}
      Based on the above information, identify potential causes for the disease.
    `;

    const models = ['gemini-2.5-flash-preview-04-17', 'gemini-2.0-flash', 'gemini-1.5'];
    let modelIndex = 0;

    while (modelIndex < models.length) {
      try {
        const result = await ai.models.generateContent({
          model: models[modelIndex],
          contents: [prompt],
          config: {
            maxOutputTokens: 500,
            temperature: 0.7,
          },
        });

        if (result.text) {
          setResponse(result.text);
          break;
        } else {
          setResponse('No potential causes identified. Please try again.');
        }
      } catch (error) {
        console.error(`Error with model ${models[modelIndex]}:`, error.message);
        if (modelIndex === models.length - 1) {
          setResponse('All models failed. Please try again later.');
        }
      }
      modelIndex++;
    }

    setLoading(false);
  };

  const specificQuestions = {
    "Tinea corporis (Ringworm)": [
      "Have you been in contact with someone who has ringworm?",
      "Have you been using public showers or locker rooms recently?",
    ],
    "Eczema": [
      "Have you been exposed to any known allergens or irritants?",
      "Is your skin often dry and itchy?",
    ],
    "Psoriasis": [
      "Have you experienced recent stress or illness?",
      "Do you have a family history of psoriasis?",
    ],
    "Acne": [
      "Have you been using any new skincare products or makeup?",
      "Do you have a diet high in sugary or processed foods?",
    ],
    // Add more disease-specific questions here
  };

  const questions = specificQuestions[diseaseName] || [
    "Have you noticed any recent changes in your skin?",
    "Do you have any known allergies?",
  ];

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2>Cause Prediction</h2>
      </header>
      <p>Answer the following questions to help us predict potential causes of your skin condition, <strong>{diseaseName}</strong>:</p>
      <form onSubmit={handleSubmit}>
        <label>
          {questions[0]}
          <input type="text" name="question1" required />
        </label>
        <br />
        <label>
          {questions[1]}
          <input type="text" name="question2" required />
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? 'Analyzing...' : 'Submit'}
        </button>
      </form>
      {response && <p>Prediction: {response}</p>}
    </div>
  );
}

export default CausePrediction;
