import React, { useState, useEffect } from 'react';

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100vh',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#000000',
    color: 'white',
    backgroundImage: 'url("/1.jpg")',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    padding: '20px',
    overflow: 'hidden',
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000,
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#61dafb',
    background: 'rgba(0, 0, 0, 0.8)',
    padding: '15px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  title: {
    marginTop: '100px',
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#61dafb',
    textAlign: 'center',
    marginBottom: '20px',
  },
  leftPanel: {
    width: '100%',
    maxWidth: '600px',
    background: 'rgba(0, 0, 0, 0.85)',
    padding: '25px',
    borderRadius: '15px',
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.7)',
    textAlign: 'center',
    margin: '20px auto',
  },
  iframe: {
    width: '100%',
    height: '400px',
    border: 'none',
    borderRadius: '12px',
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.6)',
    marginTop: '20px',
  },
  error: {
    color: 'red',
    fontSize: '1.2rem',
    fontWeight: 'bold',
    marginBottom: '20px',
  },
  paragraph: {
    fontSize: '1.1rem',
    color: '#ddd',
    lineHeight: '1.6',
    marginBottom: '20px',
  },
};

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        () => {
          setError('Unable to retrieve your location. Please enable location services.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, []);

  return (
    <div style={styles.container}>
      <header style={styles.header}>Nearby Dermatologists</header>
      <h2 style={styles.title}>Find Dermatologists Near You</h2>
      <div style={styles.leftPanel}>
        {error && <p style={styles.error}>{error}</p>}
        {location ? (
          <>
            <p style={styles.paragraph}>
              Find dermatologists near your location using the map below:
            </p>
            <iframe
              title="Nearby Dermatologists"
              src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
              style={styles.iframe}
              allowFullScreen
              loading="lazy"
            ></iframe>
          </>
        ) : (
          !error && <p style={styles.paragraph}>Loading your location...</p>
        )}
      </div>
    </div>
  );
}

export default NearbyDermatologists;
