import React, { useState, useEffect } from 'react';

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    height: '100vh', // Ensure the container takes the full viewport height
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#000000', // Black background
    color: 'white',
    overflow: 'hidden', // Prevent scrolling
  },
  leftPanel: {
    flex: 2,
    color: 'white',
    padding: '20px', // Reduce padding to fit content
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start', // Align content to the top
    gap: '10px', // Adjust spacing between elements
  },
  rightPanel: {
    flex: 1,
    backgroundImage: 'url("/1.jpg")', // Use the image as background
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
  },
  iframe: {
    width: '100%',
    height: '400px', // Reduce height to fit within the viewport
    border: 'none',
    borderRadius: '12px', // Add rounded corners
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.6)', // Add shadow for depth
  },
  error: {
    color: 'red',
    fontSize: '1.2rem', // Make error text more prominent
    fontWeight: 'bold',
  },
  header: {
    fontSize: '1.8rem', // Slightly reduce font size
    marginBottom: '10px',
    color: '#61dafb', // Highlighted color for the header
  },
  paragraph: {
    fontSize: '1rem', // Slightly reduce font size
    color: '#ddd',
    lineHeight: '1.4', // Adjust line height for better spacing
  },
};

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        () => {
          setError('Unable to retrieve your location. Please enable location services.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.leftPanel}>
        <header>
          <h2 style={styles.header}>Nearby Dermatologists</h2>
          {error && <p style={styles.error}>{error}</p>}
          {location ? (
            <>
              <p style={styles.paragraph}>
                Find dermatologists near your location using the map below:
              </p>
              <iframe
                title="Nearby Dermatologists"
                src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
                style={styles.iframe}
                allowFullScreen
                loading="lazy"
              ></iframe>
            </>
          ) : (
            !error && <p style={styles.paragraph}>Loading your location...</p>
          )}
        </header>
      </div>
      <div style={styles.rightPanel}></div>
    </div>
  );
}

export default NearbyDermatologists;
