import React, { useState } from 'react';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({ apiKey: process.env.REACT_APP_GOOGLE_GENAI_API_KEY });

const styles = {
  container: {
    textAlign: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif',
    color: 'white',
    backgroundImage: 'url("/1.jpg")',
    backgroundSize: 'auto',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'calc(100% - 150px) center',
    backgroundColor: '#000000',
    minHeight: '100vh',
    paddingTop: '100px', // Adjust padding to account for the fixed header
  },
  header: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000,
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#61dafb',
    background: 'rgba(0, 0, 0, 0.8)',
    padding: '15px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  questionContainer: {
    margin: '20px auto',
    maxWidth: '600px',
    textAlign: 'left',
    background: 'rgba(0, 0, 0, 0.7)',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.6)',
  },
  questionLabel: {
    display: 'block',
    marginBottom: '10px',
    fontSize: '1.1rem',
    color: '#ddd',
  },
  input: {
    width: '100%',
    padding: '10px',
    marginBottom: '15px',
    borderRadius: '8px',
    border: '1px solid #666',
    fontSize: '1rem',
    backgroundColor: '#222',
    color: 'white',
  },
  button: {
    padding: '12px 24px',
    background: 'linear-gradient(90deg, #61dafb, #21a1f1)',
    border: 'none',
    borderRadius: '8px',
    color: 'white',
    fontSize: '1.1rem',
    cursor: 'pointer',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  },
  loading: {
    fontSize: '1.2rem',
    color: '#61dafb',
    marginTop: '20px',
  },
  response: {
    marginTop: '20px',
    fontSize: '1.1rem',
    color: '#ddd',
    background: 'rgba(0, 0, 0, 0.7)',
    padding: '15px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.6)',
  },
  title: {
    fontSize: '2rem',
    fontWeight: 'bold',
    color: '#61dafb',
    marginBottom: '20px',
  },
};

function CausePrediction({ diseaseName }) {
  const [questions, setQuestions] = useState([]);
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  const generateQuestions = async () => {
    setLoading(true);
    setQuestions([]);
    setResponse(null);

    const prompt = `
      Generate a list of specific and relevant questions to help identify potential causes for the following skin condition:
      Disease: ${diseaseName}
    `;

    const models = ['gemini-2.5-flash-preview-04-17', 'gemini-2.0-flash', 'gemini-1.5'];
    let modelIndex = 0;

    while (modelIndex < models.length) {
      try {
        const result = await ai.models.generateContent({
          model: models[modelIndex],
          contents: [prompt],
          config: {
            maxOutputTokens: 200,
            temperature: 0.7,
          },
        });

        if (result.text) {
          const generatedQuestions = result.text.split('\n').filter((q) => q.trim() !== '');
          setQuestions(generatedQuestions); // Use all generated questions
          break;
        }
      } catch (error) {
        console.error(`Error with model ${models[modelIndex]}:`, error.message);
        if (modelIndex === models.length - 1) {
          setQuestions(['Failed to generate questions. Please try again later.']);
        }
      }
      modelIndex++;
    }

    setLoading(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResponse(null);

    const answers = Array.from(e.target.elements)
      .filter((el) => el.name.startsWith('question'))
      .map((el) => el.value);

    const prompt = `
      Disease: ${diseaseName}
      ${answers.map((answer, index) => `Question ${index + 1}: ${answer}`).join('\n')}
      Based on the above information, identify potential causes for the disease.
    `;

    const models = ['gemini-2.5-flash-preview-04-17', 'gemini-2.0-flash', 'gemini-1.5'];
    let modelIndex = 0;

    while (modelIndex < models.length) {
      try {
        const result = await ai.models.generateContent({
          model: models[modelIndex],
          contents: [prompt],
          config: {
            maxOutputTokens: 500,
            temperature: 0.7,
          },
        });

        if (result.text) {
          setResponse(result.text);
          break;
        } else {
          setResponse('No potential causes identified. Please try again.');
        }
      } catch (error) {
        console.error(`Error with model ${models[modelIndex]}:`, error.message);
        if (modelIndex === models.length - 1) {
          setResponse('All models failed. Please try again later.');
        }
      }
      modelIndex++;
    }

    setLoading(false);
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2>Cause Prediction</h2>
      </header>
      <h2 style={styles.title}>Cause Prediction</h2>
      <p>Answer the following questions to help us predict potential causes of your skin condition, <strong>{diseaseName}</strong>:</p>
      {questions.length === 0 && !loading && (
        <button onClick={generateQuestions} style={styles.button}>
          Generate Questions
        </button>
      )}
      {loading && <p style={styles.loading}>Loading...</p>}
      {questions.length > 0 && (
        <form onSubmit={handleSubmit} style={styles.questionContainer}>
          {questions.map((question, index) => (
            <div key={index}>
              <label style={styles.questionLabel}>
                {question}
                <input type="text" name={`question${index + 1}`} style={styles.input} required />
              </label>
            </div>
          ))}
          <button type="submit" style={styles.button} disabled={loading}>
            {loading ? 'Analyzing...' : 'Submit'}
          </button>
        </form>
      )}
      {response && <div style={styles.response}>Prediction: {response}</div>}
    </div>
  );
}

export default CausePrediction;
