import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import About from './About';
import Feedback from './Feedback';
import FoodRecommendation from './FoodRecommendation';
import CausePrediction from './CausePrediction';
import NearbyDermatologists from './NearbyDermatologists';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({ apiKey: 'AIzaSyAZaicXqdsZlwt1-TE-iKyB50sOG7_QiRw' });

const styles = {
  app: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif',
    color: 'white',
  },
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px 30px',
    background: 'rgba(0, 0, 0, 0.8)', // Semi-transparent black for contrast
    color: 'white',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  navTitle: {
    margin: 0,
    fontSize: '2rem',
    textAlign: 'left',
    fontWeight: 'bold',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '25px',
    margin: 0,
    padding: 0,
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    fontSize: '1.1rem',
    transition: 'color 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '20px',
  },
};

function App() {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    document.body.style.backgroundImage = 'url("/1.jpg")'; // Set background image
    document.body.style.backgroundSize = 'auto'; // Preserve original size
    document.body.style.backgroundRepeat = 'no-repeat';
    document.body.style.backgroundPosition = 'calc(100% - 150px) center'; // Move closer to the center from the right edge
    document.body.style.backgroundColor = '#000000'; // Fallback black color
    document.body.style.color = 'white'; // Default text color
    document.body.style.margin = '0';
    document.body.style.minHeight = '100vh';
  }, []);

  const handleFileInput = async (e) => {
    const file = e.target.files[0];
    if (file) {
      try {
        console.log("File selected:", file); // Log the selected file
        setSelectedImage(URL.createObjectURL(file)); // Display the selected image
        setLoading(true);
        setResult(null); // Clear previous results

        // Upload the image using the Files API
        console.log("Uploading file...");
        const uploadedFile = await ai.files.upload({
          file,
          config: { mimeType: file.type },
        });

        // Generate content using the uploaded file
        console.log("Generating content with file URI:", uploadedFile.uri);
        const response = await ai.models.generateContent({
          model: 'gemini-2.0-flash',
          contents: [
            {
              uri: uploadedFile.uri, // Use the uploaded file's URI
              mimeType: uploadedFile.mimeType,
            },
            { text: 'Analyze this skin condition image and provide details in the following format: Disease Name, Medications, Severity, Quick Remedies.' },
          ],
        });

        console.log("Raw API Response:", response); // Log the entire raw response
        console.log("Response Text:", response.text); // Log the text part specifically

        if (response.text) {
          try {
            const jsonResponse = JSON.parse(response.text); // Parse the response as JSON
            console.log("Parsed JSON Response:", jsonResponse); // Log parsed JSON
            setResult(jsonResponse); // Store the parsed JSON result
          } catch (parseError) {
            console.error('Error parsing JSON response:', parseError, "Raw text was:", response.text); // Log the text that failed parsing
            setResult('The response could not be parsed as JSON. Please try again.');
          }
        } else {
          console.warn('No result found in the response.');
          setResult('No result found. Please try again.');
        }
      } catch (error) {
        console.error('Error during file processing:', error);
        if (error.message.includes('503')) {
          setResult('The model is currently overloaded. Please try again later.');
        } else {
          setResult('An unexpected error occurred. Please try again.');
        }
      } finally {
        setLoading(false);
      }
    } else {
      console.warn("No file was selected.");
    }
  };

  const triggerFileInput = () => {
    document.getElementById('fileInput').click();
  };

  return (
    <Router>
      <div style={styles.app}>
        <nav style={styles.nav}>
          <h1 style={styles.navTitle}>SMART DERMATOLOGY</h1>
          <ul style={styles.navLinks}>
            <li><Link to="/" style={styles.navLink}>Home</Link></li>
            <li><Link to="/about" style={styles.navLink}>About</Link></li>
            <li><Link to="/feedback" style={styles.navLink}>Feedback</Link></li>
            <li><Link to="/food-recommendation" style={styles.navLink}>Food Recommendation</Link></li>
            <li><Link to="/cause-prediction" style={styles.navLink}>Cause Prediction</Link></li>
            <li><Link to="/nearby-dermatologists" style={styles.navLink}>Nearby Dermatologists</Link></li>
          </ul>
        </nav>
        <div style={styles.content}>
          <Routes>
            <Route path="/" element={
              <section
                style={{
                  position: 'absolute',
                  top: '50%',
                  left: '50%',
                  transform: 'translate(-50%, -50%)', // Center the section both vertically and horizontally
                  textAlign: 'center', // Center-align content
                  background: 'rgba(0, 0, 0, 0.9)', // Darker semi-transparent background for better readability
                  padding: '40px',
                  borderRadius: '15px', // Larger border radius for a modern look
                  boxShadow: '0 10px 20px rgba(0, 0, 0, 0.7)', // Enhanced shadow for depth
                  maxWidth: '600px', // Limit width for better readability
                  width: '90%', // Responsive width
                }}
              >
                <h2 style={{ fontSize: '2rem', marginBottom: '20px', color: '#61dafb' }}>Welcome to Smart Dermatology</h2>
                <p style={{ fontSize: '1.1rem', marginBottom: '25px', color: '#ddd' }}>
                  Upload an image to get a general analysis of the skin condition.
                </p>
                {/* Disclaimer */}
                <p style={{
                  fontSize: '0.9rem',
                  fontStyle: 'italic',
                  color: '#bbb',
                  marginTop: '20px',
                  borderTop: '1px solid #444',
                  paddingTop: '15px',
                }}>
                  <strong>Disclaimer:</strong> This tool provides general information based on AI analysis and is not a substitute for professional medical diagnosis or advice. Consult a qualified dermatologist for any health concerns.
                </p>
                <button
                  onClick={triggerFileInput}
                  style={{
                    padding: '15px 30px',
                    fontSize: '1.2rem',
                    background: 'linear-gradient(90deg, #61dafb, #21a1f1)',
                    border: 'none',
                    borderRadius: '10px', // Larger border radius for a modern look
                    color: 'white',
                    cursor: 'pointer',
                    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                    marginTop: '25px',
                  }}
                >
                  Check Your Skin
                </button>
                <input
                  id="fileInput"
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleFileInput}
                />
                {loading && <p style={{ marginTop: '20px', fontStyle: 'italic', color: '#61dafb' }}>Analyzing image, please wait...</p>}
                {selectedImage && (
                  <div style={{ marginTop: '25px' }}>
                    <img src={selectedImage} alt="Selected skin condition" style={{
                      maxWidth: '100%',
                      maxHeight: '350px', // Larger preview height
                      borderRadius: '12px',
                      marginTop: '20px',
                      display: 'block',
                      marginLeft: 'auto',
                      marginRight: 'auto',
                      boxShadow: '0 6px 12px rgba(0, 0, 0, 0.6)', // Add shadow for better visibility
                    }} />
                  </div>
                )}
                {result && (
                  <div style={{ marginTop: '25px' }}>
                    <h3 style={{ fontSize: '1.6rem', marginBottom: '20px', color: '#61dafb' }}>Analysis Result:</h3>
                    {typeof result === 'string' ? (
                      <p style={{ fontSize: '1.1rem', color: '#ddd' }}>{result}</p>
                    ) : (
                      <ul style={{ listStyle: 'none', padding: 0, fontSize: '1.1rem', color: '#ddd' }}>
                        {Object.entries(result).map(([key, value]) => (
                          <li key={key} style={{ marginBottom: '12px' }}>
                            <strong style={{ color: '#61dafb' }}>{key}:</strong> {value}
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                )}
              </section>
            } />
            <Route path="/about" element={<About />} />
            <Route path="/feedback" element={<Feedback />} />
            <Route path="/food-recommendation" element={<FoodRecommendation />} />
            <Route path="/cause-prediction" element={<CausePrediction />} />
            <Route path="/nearby-dermatologists" element={<NearbyDermatologists />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
