import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import About from './About'; // Assuming these components exist
import Feedback from './Feedback'; // Assuming these components exist
import FoodRecommendation from './FoodRecommendation'; // Assuming these components exist
import CausePrediction from './CausePrediction'; // Assuming these components exist
import NearbyDermatologists from './NearbyDermatologists'; // Assuming these components exist
// CORRECT IMPORT for the standard Google Generative AI SDK
import { GoogleGenerativeAI, HarmCategory, HarmBlockThreshold } from "@google/generative-ai";

// --- SECURITY WARNING ---
// DO NOT HARDCODE YOUR API KEY IN PRODUCTION FRONTEND CODE.
// Use a backend proxy to protect your key.
// This is for demonstration purposes ONLY.
const API_KEY = 'AIzaSyAZaicXqdsZlwt1-TE-iKyB50sOG7_QiRw'; // REPLACE WITH YOUR ACTUAL KEY (stored securely in a real app)
// --- END SECURITY WARNING ---

// Initialize the Gemini client
const genAI = new GoogleGenerativeAI(API_KEY);

// Configuration for the generative model (optional, but good for safety)
const generationConfig = {
    temperature: 0.4, // Controls randomness - lower is more deterministic
    topK: 32,
    topP: 1,
    maxOutputTokens: 4096, // Adjust as needed
};

// Safety settings (important for medical-related content)
// Adjust these based on your use case and testing
const safetySettings = [
    { category: HarmCategory.HARM_CATEGORY_HARASSMENT, threshold: HarmBlockThreshold.BLOCK_MEDIUM_AND_ABOVE },
    { category: HarmCategory.HARM_CATEGORY_HATE_SPEECH, threshold: HarmBlockThreshold.BLOCK_MEDIUM_AND_ABOVE },
    { category: HarmCategory.HARM_CATEGORY_SEXUALLY_EXPLICIT, threshold: HarmBlockThreshold.BLOCK_MEDIUM_AND_ABOVE },
    { category: HarmCategory.HARM_CATEGORY_DANGEROUS_CONTENT, threshold: HarmBlockThreshold.BLOCK_MEDIUM_AND_ABOVE },
    // Consider blocking medical advice if the model isn't fine-tuned or validated for it
