// Helper function to convert File to Base64
const fileToGenerativePart = async (file) => {
  const base64EncodedDataPromise = new Promise((resolve) => {
    const reader = new FileReader();
    reader.onloadend = () => resolve(reader.result.split(',')[1]); // Get Base64 part
    reader.readAsDataURL(file);
  });
  return {
    inlineData: { data: await base64EncodedDataPromise, mimeType: file.type },
  };
};

function App() {
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null); // Separate state for errors
  const [loading, setLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null); // Store Blob URL for display

  useEffect(() => {
  document.body.style.backgroundImage = 'url("/1.jpg")'; // Set background image
  document.body.style.backgroundSize = 'auto'; // Preserve original size
  document.body.style.backgroundRepeat = 'no-repeat';
  document.body.style.backgroundPosition = 'calc(100% - 150px) center'; // Move closer to the center from the right edge
  document.body.style.backgroundColor = '#000000'; // Fallback black color
  document.body.style.color = 'white'; // Default text color
  document.body.style.margin = '0';
  document.body.style.minHeight = '100vh';
  }, []);

  const handleFileInput = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Basic check for image type
    if (!file.type.startsWith('image/')) {
      setError('Please select an image file.');
      setSelectedImage(null);
      setResult(null);
      return;
    }

    // Display the selected image
    const imageUrl = URL.createObjectURL(file);
    setSelectedImage(imageUrl);

    // Reset previous results/errors
    setResult(null);
    setError(null);
    setLoading(true);

    try {
      // 1. Get the generative model instance
      const model = genAI.getGenerativeModel({
        model: 'gemini-1.5-flash-latest', // Use a suitable multimodal model
        safetySettings,
        generationConfig,
      });

      // 2. Prepare image data
      const imagePart = await fileToGenerativePart(file);

      // 3. Define the text prompt - Explicitly ask for JSON
      const prompt = `Analyze the provided image of a skin condition. Respond ONLY with a valid JSON object containing the following keys: "diseaseName" (string), "potentialMedications" (string, list common types or classes), "estimatedSeverity" (string, e.g., Mild, Moderate, Severe), "quickRemedies" (string, suggest general non-prescription relief measures). Do not include any introductory text, explanations, markdown formatting, or anything outside the JSON structure. If the image is unclear or not a skin condition, return a JSON object with a key "error" describing the issue. Provide general information only, not a diagnosis.`;

      // 4. Generate content
      const generationResult = await model.generateContent([prompt, imagePart]);

      // 5. Process the response
      const response = generationResult.response;
      const responseText = response.text();

      console.log('Raw API Response Text:', responseText); // Log raw response for debugging

      try {
        // Attempt to parse the response text as JSON
        const parsedJson = JSON.parse(responseText);
        if (parsedJson.error) {
          setError(`Analysis Error: ${parsedJson.error}`);
          setResult(null);
        } else {
          setResult(parsedJson); // Store the parsed JSON object
          setError(null); // Clear any previous errors
        }
      } catch (parseError) {
        console.error('Error parsing JSON response:', parseError);
        console.error('Raw text that failed parsing:', responseText);
        setError('Failed to parse the analysis result. The model might have returned an unexpected format.');
        setResult(null); // Clear previous results
      }
    } catch (apiError) {
      console.error('Error calling Gemini API:', apiError);
      // Handle specific API errors (e.g., safety blocks, quota issues)
      if (apiError.message.includes('503') || apiError.message.includes('overloaded')) {
        setError('The analysis service is temporarily overloaded. Please try again later.');
      } else if (apiError.message.includes('API key not valid')) {
        setError('API Key is invalid. Please check your configuration.');
      } else if (apiError.response?.promptFeedback?.blockReason) {
        // Handle content safety blocks
        setError(`Request blocked due to: ${apiError.response.promptFeedback.blockReason}. Please try a different image or prompt.`);
        console.error('Safety Feedback:', apiError.response.promptFeedback);
      } else {
        setError('An unexpected error occurred during analysis. Please check the console for details.');
      }
      setResult(null); // Clear results on error
    } finally {
      setLoading(false);
    }
  };

  const triggerFileInput = () => {
    document.getElementById('fileInput').click();
  };

  return (
    <Router>
      <div style={styles.app}>
        <nav style={styles.nav}>
          <h1 style={styles.navTitle}>SMART DERMATOLOGY</h1>
          <ul style={styles.navLinks}>
            <li><Link to="/" style={styles.navLink} className="nav-link-hover">Home</Link></li>
            <li><Link to="/about" style={styles.navLink} className="nav-link-hover">About</Link></li>
            <li><Link to="/feedback" style={styles.navLink} className="nav-link-hover">Feedback</Link></li>
            <li><Link to="/food-recommendation" style={styles.navLink} className="nav-link-hover">Food Recommendation</Link></li>
            <li><Link to="/cause-prediction" style={styles.navLink} className="nav-link-hover">Cause Prediction</Link></li>
