                    <img src={selectedImage} alt="Selected" style={{
                      maxWidth: '100%',
                      borderRadius: '8px',
                      marginTop: '20px',
                    }} />
                  </div>
                )}

                {/* Analysis Result Display */}
                {result && typeof result === 'object' && !error && (
                  <div style={{ marginTop: '20px' }}>
                    <h3>Analysis Result:</h3>
                    {/* Check for specific keys before rendering */}
                    <ul style={styles.resultList}>
                       {result.diseaseName && <li style={styles.resultItem}><strong style={styles.resultKey}>Condition:</strong> {result.diseaseName}</li>}
                       {result.potentialMedications && <li style={styles.resultItem}><strong style={styles.resultKey}>Potential Medications:</strong> {result.potentialMedications}</li>}
                       {result.estimatedSeverity && <li style={styles.resultItem}><strong style={styles.resultKey}>Estimated Severity:</strong> {result.estimatedSeverity}</li>}
                       {result.quickRemedies && <li style={styles.resultItem}><strong style={styles.resultKey}>General Relief Measures:</strong> {result.quickRemedies}</li>}
                    </ul>
                  </div>
                )}
              </section>
            } />
            {/* Ensure these components are correctly imported and defined */}
            <Route path="/about" element={<About />} />
            <Route path="/feedback" element={<Feedback />} />
            <Route path="/food-recommendation" element={<FoodRecommendation />} />
            <Route path="/cause-prediction" element={<CausePrediction />} />
            <Route path="/nearby-dermatologists" element={<NearbyDermatologists />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

