import React, { useState, useEffect } from 'react';

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'row',
    height: '100vh',
    fontFamily: 'Arial, sans-serif',
    backgroundColor: '#000000',
    color: 'white',
    overflow: 'hidden',
    paddingTop: '80px', // Add padding to account for the fixed header
  },
  header: {
    position: 'fixed', // Make the header fixed
    top: 0,
    left: 0,
    right: 0,
    zIndex: 1000, // Ensure it stays above other content
    fontSize: '1.8rem',
    marginBottom: '10px',
    color: '#61dafb',
    background: 'rgba(0, 0, 0, 0.8)', // Semi-transparent background
    padding: '15px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  leftPanel: {
    flex: 2,
    color: 'white',
    padding: '20px', // Reduce padding to fit content
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start', // Align content to the top
    gap: '10px', // Adjust spacing between elements
  },
  rightPanel: {
    flex: 1,
    backgroundImage: 'url("/1.jpg")', // Use the image as background
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
  },
  iframe: {
    width: '100%',
    height: '400px', // Reduce height to fit within the viewport
    border: 'none',
    borderRadius: '12px', // Add rounded corners
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.6)', // Add shadow for depth
  },
  error: {
    color: 'red',
    fontSize: '1.2rem', // Make error text more prominent
    fontWeight: 'bold',
  },
  paragraph: {
    fontSize: '1rem', // Slightly reduce font size
    color: '#ddd',
    lineHeight: '1.4', // Adjust line height for better spacing
  },
};

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        () => {
          setError('Unable to retrieve your location. Please enable location services.');
        }
      );
    } else {
      setError('Geolocation is not supported by your browser.');
    }
  }, []);

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        {/* Header remains fixed */}
      </header>
      <h2 style={{ ...styles.header, position: 'relative', marginBottom: '20px' }}>
        Nearby Dermatologists
      </h2>
      <div style={styles.leftPanel}>
        {error && <p style={styles.error}>{error}</p>}
        {location ? (
          <>
            <p style={styles.paragraph}>
              Find dermatologists near your location using the map below:
            </p>
            <iframe
              
              src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
              style={styles.iframe}
              allowFullScreen
              loading="lazy"
            ></iframe>
          </>
        ) : (
          !error && <p style={styles.paragraph}>Loading your location...</p>
        )}
      </div>
      <div style={styles.rightPanel}></div>
    </div>
  );
}

export default NearbyDermatologists;
