import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import About from './About';
import Feedback from './Feedback';
import FoodRecommendation from './FoodRecommendation';
import CausePrediction from './CausePrediction';
import NearbyDermatologists from './NearbyDermatologists';
import React, { useState } from 'react';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({ apiKey: 'AIzaSyAZaicXqdsZlwt1-TE-iKyB50sOG7_QiRw' });

const styles = {
  app: {
    textAlign: 'center',
    background: 'linear-gradient(135deg, #000000, #000000)',
    backgroundSize: '400% 400%',
    animation: 'gradientAnimation 10s ease infinite',
    color: 'white',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif',
    position: 'relative',
    padding: '20px',
  },
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px 30px',
    background: 'linear-gradient(90deg, #000000, #000000)',
    color: 'white',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  navTitle: {
    margin: 0,
    fontSize: '2rem',
    textAlign: 'left',
    fontWeight: 'bold',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '25px',
    margin: 0,
    padding: 0,
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    fontSize: '1.1rem',
    transition: 'color 0.3s ease',
  },
  header: {
    background: 'linear-gradient(135deg, #000000, #000000)',
    padding: '20px',
    textAlign: 'left',
    width: '35%',
    position: 'absolute',
    top: '50%',
    left: '10%',
    transform: 'translateY(-50%)',
    borderRadius: '12px',
    boxShadow: '0 8px 12px rgba(255, 255, 255, 0.2)',
  },
  headerTitle: {
    fontSize: '1.5rem',
    marginBottom: '15px',
    color: '#61dafb',
  },
  headerText: {
    fontSize: '1rem',
    marginBottom: '20px',
    lineHeight: '1.5',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px',
  },
  inputFile: {
    padding: '8px',
    fontSize: '0.9rem',
    backgroundColor: '#444444',
    color: 'white',
    border: '1px solid #666666',
    borderRadius: '6px',
    transition: 'background-color 0.3s ease, border-color 0.3s ease',
  },
  button: {
    padding: '10px 20px',
    fontSize: '1rem',
    background: 'linear-gradient(90deg, #61dafb, #21a1f1)',
    border: 'none',
    borderRadius: '6px',
    color: 'white',
    cursor: 'pointer',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  },
  analysisResult: {
    backgroundColor: '#ffffff',
    border: '1px solid #ddd',
    borderRadius: '8px',
    padding: '16px',
    marginTop: '16px',
    lineHeight: '1.6',
    fontFamily: 'Arial, sans-serif',
    textAlign: 'left',
  },
  analysisResultTitle: {
    marginBottom: '12px',
    color: '#333',
  },
  resultDetails: {
    margin: '8px 0',
    fontSize: '16px',
    color: '#555',
  },
  error: {
    color: 'red',
    fontWeight: 'bold',
  },
};

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
  };

  const analyzeImage = async () => {
    if (!image) {
      alert('Please upload an image first.');
      return;
    }

    setLoading(true);

    try {
      // Convert image to base64
      const reader = new FileReader();
      reader.onloadend = async () => {
        const base64Image = reader.result.split(',')[1];

        // Call Gemini API
        const response = await ai.models.generateContent({
          model: 'gemini-2.0-flash',
          contents: [
            {
              inlineData: {
                mimeType: image.type, // Dynamically set MIME type
                data: base64Image,
              },
            },
            { text: 'Analyze this skin condition image and provide details in the following format: Disease Name, Medications, Severity, Quick Remedies.' },
          ],
        });

        if (response.text) {
          setResult(formatApiResponse(response.text)); // Format the response
        } else {
          setResult('No result found. Please try again.');
        }
      };

      reader.onerror = () => {
        setResult('Error reading the image file. Please try again.');
        setLoading(false);
      };

      reader.readAsDataURL(image);
    } catch (error) {
      console.error('Error analyzing the image:', error);
      setResult('Error analyzing the image. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Function to format the API response
  const formatApiResponse = (apiResponse) => {
    const lines = apiResponse.split('\n').filter(line => line.trim() !== '');
    const formattedResult = lines.map(line => {
      const [key, value] = line.split(':').map(part => part.trim());
      return { key, value };
    });
    return formattedResult;
  };

  return (
    <Router>
      <div style={styles.app}>
        {/* Navigation Bar */}
        <nav style={styles.nav}>
          <h1 style={styles.navTitle}>SMART DERMATOLOGY</h1>
          <ul style={styles.navLinks}>
            <li><Link to="/" style={styles.navLink}>Home</Link></li>
            <li><Link to="/about" style={styles.navLink}>About</Link></li>
            <li><Link to="/feedback" style={styles.navLink}>Feedback</Link></li>
            <li><Link to="/food-recommendation" style={styles.navLink}>Food Recommendation</Link></li>
            <li><Link to="/cause-prediction" style={styles.navLink}>Cause Prediction</Link></li>
            <li><Link to="/nearby-dermatologists" style={styles.navLink}>Nearby Dermatologists</Link></li>
          </ul>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={
            <section id="home">
              <header style={styles.header}>
                <h2 style={styles.headerTitle}>Welcome to Smart Dermatology</h2>
                <p style={styles.headerText}>Upload an image of your skin condition, and our model will predict the disease.</p>
                <form
                  style={styles.form}
                  onSubmit={(e) => {
                    e.preventDefault();
                    analyzeImage();
                  }}
                >
                  <input
                    type="file"
                    accept="image/jpeg, image/png, image/webp" // Allow multiple image formats
                    onChange={handleImageUpload}
                    style={styles.inputFile}
                  />
                  <button type="submit" disabled={loading || !image} style={styles.button}>
                    {loading ? 'Analyzing...' : 'Upload and Analyze'}
                  </button>
                </form>
                {result && (
                  <div style={styles.analysisResult}>
                    <h3 style={styles.analysisResultTitle}>Analysis Result:</h3>
                    {typeof result === 'string' ? (
                      <p style={styles.error}>{result}</p>
                    ) : (
                      <div>
                        {result.map((item, index) => (
                          <p key={index} style={styles.resultDetails}>
                            <strong>{item.key}:</strong> {item.value}
                          </p>
                        ))}
                      </div>
                    )}
                  </div>
                )}
              </header>
            </section>
          } />
          <Route path="/about" element={<About />} />
          <Route path="/feedback" element={<Feedback />} />
          <Route path="/food-recommendation" element={<FoodRecommendation />} />
          <Route path="/cause-prediction" element={<CausePrediction />} />
          <Route path="/nearby-dermatologists" element={<NearbyDermatologists />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
