import React, { useEffect } from 'react';
import './App.css';

function NearbyDermatologists() {
  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_MAPS_API_KEY&libraries=places&callback=initMap`;
    script.async = true;
    document.body.appendChild(script);

    window.initMap = () => {
      const map = new window.google.maps.Map(document.getElementById('map'), {
        center: { lat: 37.7749, lng: -122.4194 }, // Default to San Francisco
        zoom: 12,
      });

      const input = document.createElement('input');
      input.type = 'text';
      input.placeholder = 'Search for dermatologists';
      input.style.margin = '10px';
      input.style.padding = '5px';
      input.style.width = '300px';
      map.controls[window.google.maps.ControlPosition.TOP_LEFT].push(input);

      const searchBox = new window.google.maps.places.SearchBox(input);
      map.addListener('bounds_changed', () => {
        searchBox.setBounds(map.getBounds());
      });

      const markers = [];
      searchBox.addListener('places_changed', () => {
        const places = searchBox.getPlaces();
        if (places.length === 0) return;

        markers.forEach((marker) => marker.setMap(null));
        markers.length = 0;

        const bounds = new window.google.maps.LatLngBounds();
        places.forEach((place) => {
          if (!place.geometry || !place.geometry.location) return;

          markers.push(
            new window.google.maps.Marker({
              map,
              title: place.name,
              position: place.geometry.location,
            })
          );

          if (place.geometry.viewport) {
            bounds.union(place.geometry.viewport);
          } else {
            bounds.extend(place.geometry.location);
          }
        });
        map.fitBounds(bounds);
      });
    };
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h2>Nearby Dermatologists</h2>
        <div id="map" style={{ width: '100%', height: '500px' }}></div>
      </header>
    </div>
  );
}

export default NearbyDermatologists;
