import './App.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import About from './About';
import Feedback from './Feedback';
import FoodRecommendation from './FoodRecommendation';
import CausePrediction from './CausePrediction';
import NearbyDermatologists from './NearbyDermatologists';
import React, { useState } from 'react';
import { GoogleGenAI } from '@google/genai'; // Import Google GenAI

const ai = new GoogleGenAI({ apiKey: 'AIzaSyAZaicXqdsZlwt1-TE-iKyB50sOG7_QiRw' }); // Initialize GenAI with API key

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
  };

  const analyzeImage = async () => {
    if (!image) {
      alert('Please upload an image first.');
      return;
    }

    setLoading(true);

    // Convert image to base64
    const reader = new FileReader();
    reader.onloadend = async () => {
      const base64Image = reader.result.split(',')[1];

      // Call Google GenAI API
      try {
        const response = await ai.models.generateContent({
          model: 'gemini-2.0-flash',
          contents: `Analyze this skin condition image: data:image/jpeg;base64,${base64Image}`,
        });

        if (response.text) {
          setResult(response.text);
        } else {
          setResult('No result found. Please try again.');
        }
      } catch (error) {
        console.error('Error analyzing the image:', error);
        setResult('Error analyzing the image. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    reader.onerror = () => {
      setResult('Error reading the image file. Please try again.');
      setLoading(false);
    };

    reader.readAsDataURL(image);
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="App-nav">
          <h1>SMART DERMATOLOGY</h1>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/feedback">Feedback</Link></li>
            <li><Link to="/food-recommendation">Food Recommendation</Link></li>
            <li><Link to="/cause-prediction">Cause Prediction</Link></li>
            <li><Link to="/nearby-dermatologists">Nearby Dermatologists</Link></li>
          </ul>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={
            <section id="home">
              <header className="App-header">
                <h2>Welcome to Smart Dermatology</h2>
                <p>Upload an image of your skin condition, and our model will predict the disease.</p>
                <form
                  onSubmit={(e) => {
                    e.preventDefault();
                    analyzeImage();
                  }}
                >
                  <input
                    type="file"
                    accept="image/*" // Allow multiple image formats
                    onChange={handleImageUpload}
                  />
                  <button type="submit" disabled={loading || !image}>
                    {loading ? 'Analyzing...' : 'Upload and Analyze'}
                  </button>
                </form>
                {result && (
                  <div>
                    <h3>Analysis Result:</h3>
                    <p>{result}</p>
                  </div>
                )}
              </header>
              {/* Image on the Right */}
              <img
                src={require('./1.jpg')} // Ensure the image is in the same directory as App.js
                alt="Dermatology Illustration"
                className="App-image"
              />
            </section>
          } />
          <Route path="/about" element={<About />} />
          <Route path="/feedback" element={<Feedback />} />
          <Route path="/food-recommendation" element={<FoodRecommendation />} />
          <Route path="/cause-prediction" element={<CausePrediction />} />
          <Route path="/nearby-dermatologists" element={<NearbyDermatologists />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
