import React, { useState, useEffect } from 'react';
import './App.css';

function NearbyDermatologists() {
  const [location, setLocation] = useState(null);
  const [error, setError] = useState(null);
  const [dermatologists, setDermatologists] = useState([]);

  useEffect(() => {
    // Get user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
          // Mock dermatologist data based on location
          setDermatologists([
            { id: 1, name: "Dr. John Doe", address: "123 Skin St, Cityville", contact: "123-456-7890" },
            { id: 2, name: "Dr. Jane Smith", address: "456 Derm Ave, Townsville", contact: "987-654-3210" },
            { id: 3, name: "Dr. Emily Brown", address: "789 Care Blvd, Metropolis", contact: "555-123-4567" },
          ]);
        },
        (err) => {
          setError("Unable to retrieve your location. Please enable location services.");
        }
      );
    } else {
      setError("Geolocation is not supported by your browser.");
    }
  }, []);

  const handleAppointment = (dermatologist) => {
    alert(`Appointment booked with ${dermatologist.name} at ${dermatologist.address}. Contact: ${dermatologist.contact}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>Nearby Dermatologists</h2>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {location ? (
          <>
            <p>Find dermatologists near your location using the map below:</p>
            <iframe
              title="Nearby Dermatologists"
              src={`https://www.google.com/maps?q=dermatologists+near+${location.latitude},${location.longitude}&output=embed`}
              width="100%"
              height="500"
              style={{ border: 0 }}
              allowFullScreen=""
              loading="lazy"
            ></iframe>
            <h3>Dermatologist Details</h3>
            <ul>
              {dermatologists.map((dermatologist) => (
                <li key={dermatologist.id}>
                  <p><strong>{dermatologist.name}</strong></p>
                  <p>Address: {dermatologist.address}</p>
                  <p>Contact: {dermatologist.contact}</p>
                  <button onClick={() => handleAppointment(dermatologist)}>Book Appointment</button>
                </li>
              ))}
            </ul>
          </>
        ) : (
          !error && <p>Loading your location...</p>
        )}
      </header>
    </div>
  );
}

export default NearbyDermatologists;
