import './App.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import About from './About';
import Feedback from './Feedback';
import FoodRecommendation from './FoodRecommendation';
import CausePrediction from './CausePrediction';
import NearbyDermatologists from './NearbyDermatologists';
import React, { useState } from 'react';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({ apiKey: 'AIzaSyAZaicXqdsZlwt1-TE-iKyB50sOG7_QiRw' });

function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageUpload = (e) => {
    setImage(e.target.files[0]);
  };

  const analyzeImage = async () => {
    if (!image) {
      alert('Please upload an image first.');
      return;
    }

    setLoading(true);

    try {
      // Convert image to base64
      const reader = new FileReader();
      reader.onloadend = async () => {
        const base64Image = reader.result.split(',')[1];

        // Call Gemini API
        const response = await ai.models.generateContent({
          model: 'gemini-2.0-flash',
          contents: [
            {
              inlineData: {
                mimeType: image.type, // Dynamically set MIME type
                data: base64Image,
              },
            },
            { text: 'Analyze this skin condition image and provide details in JSON format.' },
          ],
        });

        if (response.text) {
          try {
            const parsedResult = JSON.parse(response.text); // Parse JSON response
            setResult(parsedResult);
          } catch (parseError) {
            console.error('Error parsing JSON response:', parseError);
            setResult({ error: 'Invalid response format. Please try again.' });
          }
        } else {
          setResult({ error: 'No result found. Please try again.' });
        }
      };

      reader.onerror = () => {
        setResult({ error: 'Error reading the image file. Please try again.' });
        setLoading(false);
      };

      reader.readAsDataURL(image);
    } catch (error) {
      console.error('Error analyzing the image:', error);
      setResult({ error: 'Error analyzing the image. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Router>
      <div className="App">
        {/* Navigation Bar */}
        <nav className="App-nav">
          <h1>SMART DERMATOLOGY</h1>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/feedback">Feedback</Link></li>
            <li><Link to="/food-recommendation">Food Recommendation</Link></li>
            <li><Link to="/cause-prediction">Cause Prediction</Link></li>
            <li><Link to="/nearby-dermatologists">Nearby Dermatologists</Link></li>
          </ul>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/" element={
            <section id="home">
              <header className="App-header">
                <h2>Welcome to Smart Dermatology</h2>
                <p>Upload an image of your skin condition, and our model will predict the disease.</p>
                <form
                  onSubmit={(e) => {
                    e.preventDefault();
                    analyzeImage();
                  }}
                >
                  <input
                    type="file"
                    accept="image/jpeg, image/png, image/webp" // Allow multiple image formats
                    onChange={handleImageUpload}
                  />
                  <button type="submit" disabled={loading || !image}>
                    {loading ? 'Analyzing...' : 'Upload and Analyze'}
                  </button>
                </form>
                {result && (
                  <div className="analysis-result">
                    <h3>Analysis Result:</h3>
                    {result.error ? (
                      <p className="error">{result.error}</p>
                    ) : (
                      <div className="result-details">
                        <p><strong>Disease Name:</strong> {result.diseaseName || 'Not specified'}</p>
                        <p><strong>Symptoms:</strong> {result.symptoms || 'Not specified'}</p>
                        <p><strong>Quick Remedies:</strong> {result.quickRemedies || 'Not specified'}</p>
                        <p><strong>Medications:</strong> {result.medications || 'Not specified'}</p>
                        <p><strong>Severity:</strong> {result.severity || 'Not specified'}</p>
                      </div>
                    )}
                  </div>
                )}
              </header>
              {/* Image on the Right */}
              <img
                src={require('./1.jpg')} // Ensure the image is in the same directory as App.js
                alt="Dermatology Illustration"
                className="App-image"
              />
            </section>
          } />
          <Route path="/about" element={<About />} />
          <Route path="/feedback" element={<Feedback />} />
          <Route path="/food-recommendation" element={<FoodRecommendation />} />
          <Route path="/cause-prediction" element={<CausePrediction />} />
          <Route path="/nearby-dermatologists" element={<NearbyDermatologists />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
