import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import About from './About';
import Feedback from './Feedback';
import FoodRecommendation from './FoodRecommendation';
import CausePrediction from './CausePrediction';
import NearbyDermatologists from './NearbyDermatologists';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({ apiKey: 'AIzaSyAZaicXqdsZlwt1-TE-iKyB50sOG7_QiRw' });

const styles = {
  app: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
    fontFamily: 'Arial, sans-serif',
    color: 'white',
  },
  nav: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '15px 30px',
    background: 'rgba(0, 0, 0, 0.8)', // Semi-transparent black for contrast
    color: 'white',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
  },
  navTitle: {
    margin: 0,
    fontSize: '2rem',
    textAlign: 'left',
    fontWeight: 'bold',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '25px',
    margin: 0,
    padding: 0,
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    fontSize: '1.1rem',
    transition: 'color 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '20px',
  },
};

function App() {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    document.body.style.backgroundImage = 'url("/1.jpg")'; // Set background image
    document.body.style.backgroundSize = 'auto'; // Preserve original size
    document.body.style.backgroundRepeat = 'no-repeat';
    document.body.style.backgroundPosition = 'calc(100% - 150px) center'; // Move closer to the center from the right edge
    document.body.style.backgroundColor = '#000000'; // Fallback black color
    document.body.style.color = 'white'; // Default text color
    document.body.style.margin = '0';
    document.body.style.minHeight = '100vh';
  }, []);

  const handleFileInput = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedImage(URL.createObjectURL(file)); // Display the selected image
      setLoading(true);

      try {
        const reader = new FileReader();
        reader.onloadend = async () => {
          const base64Image = reader.result.split(',')[1];

          // Send the image to Google Gemini
          const response = await ai.models.generateContent({
            model: 'gemini-2.0-flash',
            contents: [
              {
                inlineData: {
                  mimeType: file.type,
                  data: base64Image,
                },
              },
              { text: 'Analyze this skin condition image and provide details in the following format: Disease Name, Medications, Severity, Quick Remedies.' },
            ],
          });

          if (response.text) {
            setResult(response.text); // Display the output from Gemini
          } else {
            setResult('No result found. Please try again.');
          }
        };

        reader.onerror = () => {
          setResult('Error reading the image file. Please try again.');
          setLoading(false);
        };

        reader.readAsDataURL(file);
      } catch (error) {
        console.error('Error analyzing the image:', error);
        setResult('Error analyzing the image. Please try again.');
      } finally {
        setLoading(false);
      }
    }
  };

  const triggerFileInput = () => {
    document.getElementById('fileInput').click();
  };

  return (
    <Router>
      <div style={styles.app}>
        <nav style={styles.nav}>
          <h1 style={styles.navTitle}>SMART DERMATOLOGY</h1>
          <ul style={styles.navLinks}>
            <li><Link to="/" style={styles.navLink}>Home</Link></li>
            <li><Link to="/about" style={styles.navLink}>About</Link></li>
            <li><Link to="/feedback" style={styles.navLink}>Feedback</Link></li>
            <li><Link to="/food-recommendation" style={styles.navLink}>Food Recommendation</Link></li>
            <li><Link to="/cause-prediction" style={styles.navLink}>Cause Prediction</Link></li>
            <li><Link to="/nearby-dermatologists" style={styles.navLink}>Nearby Dermatologists</Link></li>
          </ul>
        </nav>
        <div style={styles.content}>
          <Routes>
            <Route path="/" element={
              <section
                style={{
                  position: 'absolute',
                  top: '50%',
                  left: '10%',
                  transform: 'translateY(-50%)',
                  textAlign: 'left',
                  background: 'rgba(0, 0, 0, 0.7)', // Optional: Add a semi-transparent background
                  padding: '20px',
                  borderRadius: '8px',
                  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.5)',
                }}
              >
                <h2>Welcome to Smart Dermatology</h2>
                <p>Click the button below to check your skin condition.</p>
                <button
                  onClick={triggerFileInput}
                  style={{
                    padding: '10px 20px',
                    fontSize: '1rem',
                    background: 'linear-gradient(90deg, #61dafb, #21a1f1)',
                    border: 'none',
                    borderRadius: '6px',
                    color: 'white',
                    cursor: 'pointer',
                    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
                  }}
                >
                  Check Your Skin
                </button>
                <input
                  id="fileInput"
                  type="file"
                  accept="image/*"
                  style={{ display: 'none' }}
                  onChange={handleFileInput}
                />
                {loading && <p>Analyzing...</p>}
                {selectedImage && (
                  <div style={{ marginTop: '20px' }}>
                    <h3>Selected Image:</h3>
                    <img src={selectedImage} alt="Selected" style={{ maxWidth: '100%', borderRadius: '8px' }} />
                  </div>
                )}
                {result && (
                  <div style={{ marginTop: '20px' }}>
                    <h3>Analysis Result:</h3>
                    <p>{result}</p>
                  </div>
                )}
              </section>
            } />
            <Route path="/about" element={<About />} />
            <Route path="/feedback" element={<Feedback />} />
            <Route path="/food-recommendation" element={<FoodRecommendation />} />
            <Route path="/cause-prediction" element={<CausePrediction />} />
            <Route path="/nearby-dermatologists" element={<NearbyDermatologists />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
