import React, { useState } from 'react';

function CausePrediction() {
  const [response, setResponse] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = {
      question1: e.target.elements.question1.value,
      question2: e.target.elements.question2.value,
    };
    // Example API call to Gemini API
    const apiResponse = await fetch('https://api.gemini.com/predict', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data),
    });
    const result = await apiResponse.json();
    setResponse(result.message);
  };

  const styles = {
    container: {
      textAlign: 'center',
      padding: '20px',
      fontFamily: 'Arial, sans-serif',
      color: 'white',
      backgroundImage: 'url("/1.jpg")',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      minHeight: '100vh',
      paddingTop: '80px', // Add padding to account for the fixed header
    },
    header: {
      position: 'fixed', // Make the header fixed
      top: 0,
      left: 0,
      right: 0,
      zIndex: 1000, // Ensure it stays above other content
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#61dafb',
      background: 'rgba(0, 0, 0, 0.8)', // Semi-transparent background
      padding: '15px',
      textAlign: 'center',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.5)',
    },
  };

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h2>Cause Prediction</h2>
      </header>
      <p>Answer the following questions to help us predict potential causes of your skin condition:</p>
      <form onSubmit={handleSubmit}>
        <label>
          Have you been exposed to any allergens recently?
          <input type="text" name="question1" required />
        </label>
        <br />
        <label>
          Do you have a history of skin conditions in your family?
          <input type="text" name="question2" required />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
      {response && <p>Prediction: {response}</p>}
    </div>
  );
}

export default CausePrediction;
